package CoreJava;

public class Forloop {  // for loop problem

	int x =10;
	void increment() {
		
		for (int x=1; x<=10; x++)
		{
			System.out.println(x);
		}
		
	}
	
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Forloop obj =new Forloop();
		obj.increment();
		
	}

}
