package CoreJava;

public class incrementoperator {
      int x=10;
     void show()

     {
    	 for(int x=1; x<=10; x++ )
    	 {
    	 System.out.println(x);
    	 }
    	 
     }
 

      
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		incrementoperator obj =new incrementoperator();
	obj.show();
	}

}
