package PatternQuestions;

public class starquestion2 {
	static void pawar()
	{
		for (int i=1; i<=4; i++)
		{
			for(int j=1; j<=i; j++)
			{
				System.out.print(" * ");
				
			}
			System.out.println(" ");
			
		}}
	
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		starquestion2.pawar();
	}

}
