package StringProblem;

public class StringWithCharAtandIndex {

	static void CharAt ()
	{
		String a = " this is bhatu pawar ";
		System.out.println(a.charAt(9));
	}
	static void Indexof ()
	{
		String a = " this is bhatu pawar ";
		System.out.println(a.indexOf(2));
	}
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		StringWithCharAtandIndex.CharAt();
		StringWithCharAtandIndex.Indexof();
	}

}
