package TestPracticefistdayTo25jan;

public class AssWk1Q7 {

	 static void m1 ()
	{
		
		  
		for (int i=10; i>=5; i--)
		{
			if(i%2==1)
			{
				System.out.println("odd num "+i);
				
			}
			else
			{
				System.out.println("no odd num "+i);	
			}
			
		}
		
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		AssWk1Q7.m1();
	}

}
